const myImage = new Image();
myImage.src = " data:image/jpeg;base64,";


myImage.addEventListener("load",() => {
    const canvas = document.querySelector("canvas");
    const c = canvas.getContext("2d");

    //set the width of the canvas
    canvas.width = 1000;
    canvas.height = 700;
    c.drawImage(myImage,0,0,canvas.width,canvas.height);
    const pixels = c.getImageData(0,0,canvas.width,canvas.height);


    let ParticlesArray = [];
    let numberofParticles = 5000;
    let mappedImage = [];


    console.log(pixels)
    for(let y=0;y<canvas.height;y++){
        let row = [];
        for(let x=0;x<canvas.width;x++){
            let red = pixels.data[(y * 4 * pixels.width) + (x * 4)];
            let green = pixels.data[(y * 4 * pixels.width) + (x * 4 + 1)];
            let blue = pixels.data[(y * 4 * pixels.width) + (x * 4 + 2)];
            let brightness = calculateRelativeBrightness(red,green,blue);
            const cell = [
                cellBrightness = brightness
            ]
            row.push(cell);
        }
        mappedImage.push(row);
    }

    console.log(mappedImage)


    function calculateRelativeBrightness(red,green,blue){
        return Math.sqrt((red * red) * 0.299 + (green * green) * 0.587 + (blue * blue) * 0.144)/100;
    }


    class Particle{
        constructor(){
            this.x = Math.random() * canvas.width;
            this.y = 0;
            this.speed = 0;
            this.velocity = Math.random() * .5;
            this.size = Math.random() * 1.5 + 1;
            this.position1 = Math.floor(this.y);
            this.position2 = Math.floor(this.x);
        }

        update(){
            
            this.speed = mappedImage[this.position1][this.position2][0];
            let movement = (2.5 - this.speed) + this.velocity;
            this.y += movement;

            if(this.y >= canvas.height){
                this.y = 0;
                this.x = Math.random() * canvas.width
            }
        }

        draw(){
            c.beginPath()
            c.fillStyle = "white";
            c.arc(this.x,this.y,this.size,0,Math.PI*2);
            c.fill();
        }
    }

    function init(){

        for(let i= 0;i<numberofParticles;i++){
            ParticlesArray.push(new Particle());
        }
    }


    init();


    function animate(){
        c.globalAlpha = 0.05;
        c.fillStyle = "rgb(0,0,0)";
        c.fillRect(0,0,canvas.width,canvas.height);
        c.globalAlpha = 0.2;
        for(let i=0;i<ParticlesArray.length;i++){
            c.globalAlpha = ParticlesArray[i].speed;
            ParticlesArray[i].update();
            ParticlesArray[i].draw();
        }
        requestAnimationFrame(animate);
    }

    
    animate()

})